const MarqueeLogo: React.FC<MarqueeLogoProps> = ({
  type = 'client',
  speed = 40,
  disableDesktop = false,
  disableMobile = false,
  direction = 'right',
}) => {
  const logos = type === 'partners' ? partnerLogos : clientLogos;

  const isMobile = typeof window !== 'undefined' && window.innerWidth <= 992;

  // Set the marquee speed to 0 if it is disabled for the current device type
  const effectiveSpeed = () => {
    if (disableDesktop && !isMobile) return 0;
    if (disableMobile && isMobile) return 0;
    return speed;
  };

  return (
    <div className='c-marquee'>
      <Marquee gradient={false} speed={effectiveSpeed()} direction={direction} className='c-marquee-logo'>
        {logos.map((logo, index) => (
          <div key={index} className="c-marquee-logo__item">
            {logo}
          </div>
        ))}
      </Marquee>
    </div>
  );
};

export default MarqueeLogo;